mapper.update:
update Choicequestion set questiontypeid=#{questiontypeid},text=#{text},option1=#{option1},option2=#{option2},option3=#{option3},option4=#{option4},option5=#{option5},
answer=#{answer}
where id=#{id}

mapper.select:
select qt.type as type,qt.level as level,qt.score as score,cq.* from Choicequestion cq left join questiontype qt on cq.questiontypeid=qt.id
